package com.example.airline_api.services;import com.example.airline_api.models.Flight;import com.example.airline_api.models.FlightDTO;import com.example.airline_api.models.Passenger;import org.springframework.beans.factory.annotation.Autowired;import com.example.airline_api.repositories.FlightRepository;import java.util.List;public class FlightService{    @Autowired    FlightRepository flightRepository;    @Autowired    PassengerService passengerService;    public Flight saveFlight(FlightDTO flightDTO)    {        Flight flight = new Flight(                flightDTO.getDestination(),                flightDTO.getCapacity(),                flightDTO.getDepartureDate(),                flightDTO.getDepartureTime()        );        for (Long passengerId : flightDTO.getPassengerIds())        {            Passenger passenger = passengerService.findPassenger(passengerId);            flight.addPassenger(passenger);        }        flightRepository.save(flight);        return flight;    }    public Flight findFlight(long id)    {        return flightRepository.findById(id).get();    }    public List<Flight> findAllFlights()    {        return flightRepository.findAll();    }    public void deleteFlight(long id)    {        flightRepository.deleteById(id);    }}